--          ,SYSDATE + (1*(1/24/60/60))
          ,systimestamp + numtodsinterval( cnt / 1000, 'SECOND' )
          ,systimestamp + numtodsinterval( cnt / 1000, 'SECOND' )
          ,'notice'
          );
    ELSE
        --공지사항글 등록
          INSERT INTO tbl_board VALUES (
          cnt
          ,'겔러리게시물제목'
          ,'겔러리게시물내용'
          ,'admin'
          ,0
          ,0
--          ,SYSDATE + (1*(1/24/60/60))
--          ,SYSDATE + (1*(1/24/60/60))
          ,'gallery'
          ,systimestamp + numtodsinterval( cnt / 1000, 'SECOND' )
          ,systimestamp + numtodsinterval( cnt / 1000, 'SECOND' )
          );
    END IF;
  END LOOP;
    commit;

END PROC_DUMMY_BOARD;

